console.log("event.min.js loaded");document.addEventListener("DOMContentLoaded",function(){console.log("DOM loaded");var form=document.getElementById("contactForm");if(!form){console.error("Form not found");return}console.log("Form found:",form);form.onsubmit=function(e){console.log("onsubmit triggered");e.preventDefault();e.stopPropagation();console.log("preventDefault called");var formData={nombre:form.nombre.value,email:form.email.value,mensaje:form.mensaje.value};console.log("Form data:",formData);var apiUrl=window.location.hostname==="localhost"?"http://localhost:3000/api/contact":"/api/contact";console.log("API URL:",apiUrl);fetch(apiUrl,{method:"POST",headers:{"Content-Type":"application/json"},body:JSON.stringify(formData)}).then(function(response){console.log("Response received:",response.status);return response.json()}).then(function(data){console.log("Data:",data);if(data.success){alert("Mensaje enviado correctamente");form.reset()}else{alert("Error: "+(data.error||"Desconocido"))}}).catch(function(error){console.error("Error:",error);alert("Error de conexion: "+error.message)});return false}});
